mapper.select:
select * from visit_probe where 1=1

    and begin_time=#{beginTime}


    and mac=#{mac}


    and assets_id=#{assetsId}

SELECT date(dday) AS visitDate, count(*) - 1 AS visitCount FROM ( SELECT datelist AS dday FROM calendar UNION ALL SELECT end_time FROM visit_probe WHERE 1=1
and assets_id = #{assetsId}
) a WHERE 1 = 1 AND a.dday between #{start} and #{end} GROUP BY visitDate

SELECT ROUND( UNIX_TIMESTAMP(t.end_time) - UNIX_TIMESTAMP(t.begin_time) ) AS visitTime, t.mac, count(t.id) as visitCount FROM visit_probe t WHERE DATE_FORMAT(t.end_time, '%Y-%m-%d') = #{time}
AND t.assets_id = #{assetsId}
GROUP BY t.mac

select count(id) from visit_probe t
where DATE_FORMAT(t.end_time, '%Y-%m-%d') =#{time}
and assets_id =#{assetsId} and is_old=1
